<form method="post"><label for="title">Title</label><input type="text" name="title" id="title"><label for="image">Image</label><input type="file" name="image" id="image"><label for="desc">Short Description</label><input type="text" name="description" id="description"><label for="category">Select Category</label><select name="category" id="category"><option value="how_to">How To</option><option value="news">News</option><option value="reviews">Reviews</option><option value="blog">Blog</option></select><label for="publish">Publish Now?</label><input type="checkbox" name="publish" id="publish"><input type="submit" value="submit"></form>